import React, { useCallback, useState } from "react";
import styled from "styled-components";

const Wrapper = styled.div`
    width: 100%;
    padding: 10px;
    margin: 10px;
`;

const WrapperTitle = styled.h3`
    color: #999;
    font-size: 17px;
`;

const ImageBox = styled.div`
    width: 860px;
    height: 600px;

    position: relative;
`;

const Image1 = styled.img`
    width: 100%;
    height: 100%;
    object-fit: cover;

    position: absolute;
    top: 0px;
    left: 0px;
`;

const ImageTitle = styled.article`
    font-size: 30px;
    color: #fff;
    font-weight: bold;
    text-shadow: 6px 6px 4px #999;

    position: absolute;
    top: 10%;
    left: 5%;
`;


const Image3 = styled.img`
    width: 100%;
    height: 100%;

    position: absolute;
    top: 0px;
    left: 0px;
`;

const MyWeb = () => {

    const [devMode, setDevMode] = useState(false);

    const [imageTitle, setimageTitle] = useState("IMAGE TITLE");

    const devModeToggleHandler = useCallback(() => {
        setDevMode((prev) => !prev);
    }, [devMode]);

    return <section>

        <button>{devMode ? "관리자 끄기" : "관리자 켜기"}</button>

        {/* 이미지와 텍스트를 관리 할 수 있게 한다. */}
        <Wrapper>
            <WrapperTitle >이미지와 텍스트를 관리 할 수 있게 한다.</WrapperTitle >
            <ImageBox>
                <Image1 src="https://i.pinimg.com/474x/84/3d/c9/843dc95e60a6082ce786baaf771cb3ab.jpg" alt="mainPoster"/>

                <ImageTitle>ImageTitle</ImageTitle>
            </ImageBox>

            {devMode && <div>제어 화면</div>}
        </Wrapper>

        {/* 이미지 슬라이드의 이미지를 관리 할수있게 */}
        <Wrapper>
            <WrapperTitle >이미지 슬라이드의 이미지를 관리 할수있게 </WrapperTitle >
        </Wrapper>

        {/* 이미지 슬라이더에 이미지와 텍스트를 관리 할 수 있게 한다. */}
        <Wrapper>
            <WrapperTitle >이미지 슬라이더에 이미지와 텍스트를 관리 할 수 있게 한다.</WrapperTitle >
        </Wrapper>
    </section>;
}

export default MyWeb;
